import NewsList from './pages/news/list.vue'
import UserList from './pages/user/list.vue'
import UserInvest from './pages/user/invest.vue'
import UserTransaction from './pages/user/transaction.vue'
import UserAssets from './pages/user/assets.vue'
import Config from './pages/config/index.vue'


let routes = [
  {
    path: '/',
    component: Home
  },
  {
    path: '/login',
    component: Login
  },
  {
    path: '/news',
    component: NewsList
  },
  {
    path: '/news/update',
    component: NewsUpdate
  },
  {
    path : '/user',
    component : UserList
  },
  {
    path : '/user/invest',
    component : UserInvest
  },
  {
    path : '/user/assets',
    component : UserAssets
  },
  {
    path: '/user/transaction',
    component: UserTransaction
  },
  {
    path : '/config',
    component : Config
  }
]

export function createRouter() {
  return new Router({
    mode: 'history',
    routes: routes

  })
}

import {
  componentRegister,
  createApp
} from './app'

componentRegister()

export default (context) => {

  return new Promise((resolve, reject) => {
    const {
      app,
      router,
      store
    } = createApp()

    // 设置服务器端 router 的位置
    router.push(context.url)

    // 等到 router 将可能的异步组件和钩子函数解析完
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents()
      // 匹配不到的路由，执行 reject 函数，并返回 404
      if (!matchedComponents.length) {
        return reject({
          code: 404
        })
      }

      // 对所有匹配的路由组件调用 `asyncData()`
      Promise.all(matchedComponents.map(Component => {
        if (Component.asyncData) {
          return Component.asyncData({
            store,
            route: router.currentRoute
          })
        }
      })).then(() => {
        // 在所有预取钩子(preFetch hook) resolve 后，
        // 我们的 store 现在已经填充入渲染应用程序所需的状态。
        // 当我们将状态附加到上下文，
        // 并且 `template` 选项用于 renderer 时，
        // 状态将自动序列化为 `window.__INITIAL_STATE__`，并注入 HTML。
        context.state = store.state

        resolve(app)
      }).catch(reject)
    }, reject)
  })

}

